('upper_lower_6x_intensive', 'Upper/Lower Intensive', 'Три цикла верх/низ — максимальная частота', 6, 45, 75, 'upper_lower', ARRAY['training_experienced'], ARRAY['build_muscle', 'strength'], ARRAY['gym_full'], '[{"day": 1, "label": "Upper Heavy", "focus": "Тяжёлые жимы и тяги"},{"day": 2, "label": "Lower Heavy", "focus": "Тяжёлые приседы и тяги"},{"day": 3, "label": "Upper Volume", "focus": "Объёмная работа верх"},{"day": 4, "label": "Lower Volume", "focus": "Объёмная работа низ"},{"day": 5, "label": "Upper Pump", "focus": "Пампинг и изоляция"},{"day": 6, "label": "Lower Pump", "focus": "Пампинг и изоляция"}]'::jsonb, ARRAY['Максимальная частота стимуляции','Разнообразие нагрузок','Очень высокий объём'], NULL, 'high', 'any'),
('push_pull_legs_6x_glutes', 'PPL + Glutes Focus', 'Push/Pull/Legs с дополнительным днём для ягодиц', 6, 45, 75, 'push_pull_legs', ARRAY['training_experienced'], ARRAY['lower_body_focus', 'build_muscle'], ARRAY['gym_full'], '[{"day": 1, "label": "Push", "focus": "Грудь, плечи, трицепс"},{"day": 2, "label": "Pull", "focus": "Спина, бицепс"},{"day": 3, "label": "Legs + Glutes A", "focus": "Квадрицепсы, ягодицы"},{"day": 4, "label": "Push", "focus": "Вариации жимов"},{"day": 5, "label": "Pull", "focus": "Вариации тяг"},{"day": 6, "label": "Glutes + Hamstrings", "focus": "Ягодицы, бицепс бедра, изоляция"}]'::jsonb, ARRAY['Максимальный фокус на ягодицы — 2 дня в неделю','Сбалансированное развитие всего тела','Идеально для женских амбиций'], NULL, 'high', 'female');
